mapper.select:
select group_id, group_code, group_name, group_order,if_use, creator_id, updator_id, created_at, updated_at from grouping where 1=1

	and group_code=#{groupCode}


	and group_name like '%' #{groupName} '%'


	and creator_id=#{creatorId}


	and created_at=#{createdAt}


	and is_deleted=#{isDeleted}
