mapper.select:
select post_id, post_code, post_name, post_order,if_use, creator_id, updator_id, created_at, updated_at from post where if_use=0

	and post_code=#{postCode}


	and post_name like '%' #{postName} '%'


	and creator_id=#{creatorId}


	and created_at=#{createdAt}


	and is_deleted=#{isDeleted}

mapper.update:
update post

    
        if_use=#{ifUse},
    

where post_code like #{postCode}
